async function sendToN8NWorkflow(message) {
  try {
    const response = await fetch('http://localhost:5678/webhook-test/3a13350f-d676-4512-9e27-2833d707f231', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        message: message,
        timestamp: new Date().toISOString(),
        sessionId: 'user-session-' + Date.now()
      })
    });

    if (!response.ok) throw new Error('网络请求失败');
    return await response.json();
  } catch (error) {
    return {
      analysis: '抱歉，AI服务暂时不可用，请稍后再试。',
      recommendations: []
    };
  }
}
